parameters.text:
=Create an image prompt for: {{ $('Prepare Dynamic Data').item.json.specificTopic }}

Business: {{ $('Prepare Dynamic Data').item.json.businessName }}
Business Category: {{ $('Prepare Dynamic Data').item.json.businessCategory }}
Content Type: {{ $('Prepare Dynamic Data').item.json.socialContentType }}

Generate a specific image prompt that visually represents this exact topic, not generic business scenes.

options.systemMessage:
=// Dynamic Image Prompt Generator - Replace your hardcoded GENERATE PROMPT system message

const dynamicData = $('Prepare Dynamic Data').item.json;
const businessCategory = dynamicData.businessCategory || '';
const specificTopic = dynamicData.specificTopic || '';
const businessName = dynamicData.businessName || '';

console.log('=== DYNAMIC IMAGE PROMPT GENERATION ===');
console.log('Business Category:', businessCategory);
console.log('Specific Topic:', specificTopic);
console.log('Business Name:', businessName);

// UNIVERSAL TOPIC ANALYSIS (works for any business)
function analyzeTopicForImageStyle(topic) {
  const topicLower = topic.toLowerCase();

  if (topicLower.includes('choose') || topicLower.includes('select') || topicLower.includes('comparison')) {
    return 'product comparison/selection imagery with multiple options displayed side by side';
  }

  if (topicLower.includes('how to') || topicLower.includes('guide') || topicLower.includes('tutorial')) {
    return 'step-by-step instructional imagery showing process stages';
  }

  if (topicLower.includes('maintenance') || topicLower.includes('care') || topicLower.includes('maintain')) {
    return 'maintenance process photography with tools and equipment';
  }

  if (topicLower.includes('safety') || topicLower.includes('protection') || topicLower.includes('secure')) {
    return 'safety demonstration imagery with proper protective equipment';
  }

  if (topicLower.includes('beginner') || topicLower.includes('starter') || topicLower.includes('youth')) {
    return 'beginner-friendly educational setup with clear, simple presentation';
  }

  if (topicLower.includes('training') || topicLower.includes('technique') || topicLower.includes('skill')) {
    return 'professional training demonstration with expert guidance';
  }

  if (topicLower.includes('problem') || topicLower.includes('issue') || topicLower.includes('troubleshoot')) {
    return 'problem diagnosis and solution imagery';
  }

  if (topicLower.includes('benefit') || topicLower.includes('advantage') || topicLower.includes('value')) {
    return 'before/after or benefit demonstration imagery';
  }

  // Default for any topic
  return 'professional demonstration of the topic with relevant tools and environment';
}

// DYNAMIC BUSINESS CATEGORY MAPPING
function getBusinessImageContext(category) {
  // Create mappings for known categories
  const knownCategories = {
    'Sports Coaching & Training': 'professional football training environment',
    'Sports Consultancy': 'professional football session plan or resource plan',
    'Automotive': 'modern automotive workshop/garage setting',
    'Plumbing': 'professional plumbing installation environment or bathroom installation', 
    'Electrical': 'electrical work setting with safety equipment',
    'Food Service': 'commercial kitchen/culinary environment',
    'Travel & Cruise': 'travel destination/cruise setting, must be Royal Caribbean brand',
    'Car Valeting': 'automotive detailing and cleaning environment on a household drive or street next to house',
    'Mortgage & Property': 'professional office consultation setting or home visit from property consultant',
    'Property': 'professional property/real estate environment',
    'Financial Services': 'professional financial consultation setting or home visit from consultant',
    'Cleaning Services': 'professional cleaning service environment'
  };

  // Return specific mapping if found
  if (knownCategories[category]) {
    return knownCategories[category];
  }

  // SMART FALLBACK: Analyze category words
  const categoryLower = category.toLowerCase();

  if (categoryLower.includes('automotive') || categoryLower.includes('car') || categoryLower.includes('vehicle')) {
    return 'automotive service environment';
  }

  if (categoryLower.includes('property') || categoryLower.includes('real estate') || categoryLower.includes('mortgage')) {
    return 'professional property/real estate setting';
  }

  if (categoryLower.includes('cleaning') || categoryLower.includes('valeting') || categoryLower.includes('maintenance')) {
    return 'professional cleaning/maintenance environment';
  }

  if (categoryLower.includes('coaching') || categoryLower.includes('training') || categoryLower.includes('education')) {
    return 'professional training/education environment';
  }

  if (categoryLower.includes('food') || categoryLower.includes('catering') || categoryLower.includes('restaurant')) {
    return 'culinary/food service environment';
  }

  if (categoryLower.includes('travel') || categoryLower.includes('cruise') || categoryLower.includes('tourism')) {
    return 'travel/destination setting';
  }

  if (categoryLower.includes('financial') || categoryLower.includes('accounting') || categoryLower.includes('consulting')) {
    return 'professional office consultation environment';
  }

  // Universal fallback for unknown categories
  return 'professional business environment with industry-appropriate tools and setting';
}

// GENERATE DYNAMIC SYSTEM MESSAGE
const topicImageStyle = analyzeTopicForImageStyle(specificTopic);
const businessImageContext = getBusinessImageContext(businessCategory);

const dynamicSystemMessage = `You are an expert at creating specific image prompts based on user topics and business context.

CRITICAL: Create image prompts that match the SPECIFIC TOPIC provided, not generic business imagery.

CURRENT REQUEST:
- Business: ${businessName}
- Category: ${businessCategory}  
- Topic: ${specificTopic}

TOPIC-BASED IMAGE STYLE:
${topicImageStyle}

BUSINESS CONTEXT:
${businessImageContext}

UNIVERSAL PROMPT CREATION RULES:
- Focus on the SPECIFIC TOPIC "${specificTopic}", not generic business scenes
- Use ${businessImageContext} as the setting/environment
- Apply ${topicImageStyle} as the main approach
- Include professional, high-quality imagery descriptions
- Show relevant tools, equipment, or materials for this business type
- Create educational/informative visual layouts when appropriate
- End with: ", no text or logos"
- Keep under 950 characters
- Make it industry-appropriate but topic-specific

DYNAMIC EXAMPLE FOR YOUR REQUEST:
Topic: "${specificTopic}"
Business Context: ${businessImageContext}
Image Style: ${topicImageStyle}

Create a prompt that combines these elements naturally and professionally.

OUTPUT REQUIREMENTS:
- Topic-specific imagery (not generic business photos)
- Industry-appropriate setting (${businessImageContext})
- Professional quality description
- Minimal text or logos, unless you have access to the businesses logo via a reliable online source like the business website.  If you are using text, please ensure the correct spelling is used.
- Under 950 characters`;

console.log('âœ… Dynamic system message generated');
console.log('Topic Analysis:', topicImageStyle);
console.log('Business Context:', businessImageContext);
console.log('Message Length:', dynamicSystemMessage.length);

return {
  json: {
    dynamicSystemMessage: dynamicSystemMessage,
    topicAnalysis: topicImageStyle,
    businessContext: businessImageContext,

    // For debugging and improvement
    categoryMapped: businessCategory,
    topicMapped: specificTopic,
    fallbackUsed: !['Sports Coaching & Training', 'Automotive', 'Plumbing', 'Electrical', 'Food Service', 'Travel & Cruise'].includes(businessCategory),

    // Track new categories for future enhancement
    newCategoryDetected: !knownCategories[businessCategory],
    suggestedCategoryMapping: businessImageContext
  }
};

parameters.jsCode:
// FIXED Prepare Dynamic Data Node - CORRECT JSON EXTRACTION
// Properly extracts from form submission JSON and business config separately

console.log('=== PROCESSING FORM-SUBMITTED CONTENT (CORRECT JSON EXTRACTION) ===');

// Get the form submission data (from Process Form Data node)
//const formSubmissionData = $json;
const formSubmissionData = $('On form submission').first().json; // Gets data directly from form
console.log('Form submission data received:', formSubmissionData);

// Get business configuration data (separate from form data)
const businessConfigs = $('Get Business Config').all();

if (!businessConfigs || businessConfigs.length === 0) {
  console.log(`âŒ ERROR: No business configuration found for: "${formSubmissionData.selectedBusinessName || formSubmissionData['Business Name']}"`);
  return [{ 
    json: { 
      error: 'Business config not found or inactive', 
      selectedBusinessName: formSubmissionData.selectedBusinessName || formSubmissionData['Business Name'],
      skip: true,
      reason: `Business not found or inactive in Business Configuration database`
    } 
  }];
}

// Get the first/only business config
const businessConfig = businessConfigs[0];
const selectedBusinessId = businessConfig.json.id;
const foundBusinessName = businessConfig.json.properties?.Title?.title?.[0]?.plain_text;

console.log('âœ… BUSINESS CONFIG LOADED:');
console.log(`  Business Name: "${foundBusinessName}"`);
console.log(`  Business ID: ${selectedBusinessId}`);

// Check if business is active
const isBusinessActive = businessConfig.json.properties?.['Active Status']?.checkbox;

if (!isBusinessActive) {
  console.log(`ðŸš« SKIPPING INACTIVE BUSINESS: ${foundBusinessName}`);
  return [{ 
    json: { 
      skip: true, 
      reason: 'Business configuration is inactive',
      businessName: foundBusinessName,
      businessId: selectedBusinessId,
      activeStatus: isBusinessActive
    } 
  }];
}

console.log(`âœ… PROCESSING ACTIVE BUSINESS: ${foundBusinessName}`);

// Helper functions for business config extraction
function getRichText(property) {
  if (!property) return '';
  if (typeof property === 'string') return property;
  if (property.rich_text && Array.isArray(property.rich_text) && property.rich_text.length > 0) {
    return property.rich_text[0].plain_text || '';
  }
  if (property.title && Array.isArray(property.title) && property.title.length > 0) {
    return property.title[0].plain_text || '';
  }
  return '';
}

function getTitle(property) {
  return property?.title?.[0]?.plain_text || '';
}

function getSelect(property) {
  return property?.select?.name || '';
}

function safeParseInt(value, fallback) {
  if (!value && value !== 0) {
    console.log(`Using fallback ${fallback} for null/undefined value`);
    return fallback;
  }

  if (typeof value === 'number' && !isNaN(value)) {
    console.log(`Value is already a number: ${value}`);
    return Math.round(value);
  }

  if (typeof value === 'string') {
    const parsed = parseInt(value.trim());
    if (!isNaN(parsed) && parsed > 0) {
      console.log(`Successfully parsed string "${value}" to number: ${parsed}`);
      return parsed;
    } else {
      console.log(`Failed to parse string "${value}", using fallback: ${fallback}`);
      return fallback;
    }
  }

  console.log(`Unexpected type ${typeof value}, using fallback: ${fallback}`);
  return fallback;
}

// ðŸŽ¯ PRIORITY 1: EXTRACT USER FORM DATA (HIGHEST PRIORITY) - CORRECT EXTRACTION
console.log('=== USER FORM DATA EXTRACTION (HIGHEST PRIORITY - FIXED) ===');

// Extract form fields using the ACTUAL JSON structure from form submission
const userBusinessName = formSubmissionData['Business Name'] || '';
const userSocialContentType = formSubmissionData['Social Content Type'] || 'Educational';
const userSpecificTopic = formSubmissionData['Specific Topic'] || '';
const userWordCountTarget = formSubmissionData['Word Count Target'] || '50';
const userProductFocusUrl = formSubmissionData['Product Focus URL'] || '';
const userCtaUrl = formSubmissionData['CTA URL'] || '';
const userSeoKeywords = formSubmissionData['SEO Keywords'] || '';
const userContentBrief = formSubmissionData['Content Brief'] || '';

console.log('âœ… USER FORM DATA EXTRACTED (CORRECTED):');
console.log('  Business Name:', userBusinessName);
console.log('  Social Content Type:', userSocialContentType);
console.log('  Specific Topic:', userSpecificTopic);
console.log('  Word Count Target:', userWordCountTarget);
console.log('  Product Focus URL:', userProductFocusUrl || 'None');
console.log('  CTA URL:', userCtaUrl || 'None');
console.log('  SEO Keywords:', userSeoKeywords || 'None');
console.log('  Content Brief Length:', userContentBrief.length);

// VALIDATION: Ensure critical form data is present
if (!userSpecificTopic || userSpecificTopic.length < 3) {
  console.log('âŒ CRITICAL ERROR: Specific Topic missing or too short');
  console.log('Specific Topic value:', userSpecificTopic);
  return [{ 
    json: { 
      error: 'Missing specific topic',
      skip: true,
      reason: 'User must provide a specific topic',
      receivedTopic: userSpecificTopic
    } 
  }];
}

if (!userContentBrief || userContentBrief.length < 20) {
  console.log('âŒ CRITICAL ERROR: Content Brief missing or too short');
  console.log('Content Brief value:', userContentBrief);
  return [{ 
    json: { 
      error: 'Missing content brief',
      skip: true,
      reason: 'User must provide detailed content brief',
      receivedBrief: userContentBrief
    } 
  }];
}

// Process word count target
const shortformWordCount = safeParseInt(userWordCountTarget, 50);
const longformWordCount = 800; // Standard longform target

// Validate word count ranges
const validatedShortform = Math.max(10, Math.min(shortformWordCount, 100));
const validatedLongform = Math.max(100, Math.min(longformWordCount, 3000));

if (validatedShortform !== shortformWordCount) {
  console.log(`âš ï¸ Shortform word count ${shortformWordCount} adjusted to ${validatedShortform} (valid range: 10-100)`);
}

const finalShortformWordCount = validatedShortform;
const finalLongformWordCount = validatedLongform;

// Calculate character estimates
const shortformCharEstimate = finalShortformWordCount * 6;
const longformCharEstimate = finalLongformWordCount * 6;

console.log('=== WORD COUNT PROCESSING ===');
console.log('User requested word count:', userWordCountTarget);
console.log('Final shortform word count:', finalShortformWordCount);
console.log('Final longform word count:', finalLongformWordCount);

// URL system detection
const hasUserProductFocusUrl = !!(userProductFocusUrl && userProductFocusUrl.trim() !== '');
const hasUserCtaUrl = !!(userCtaUrl && userCtaUrl.trim() !== '');

console.log('--- USER DUAL URL SYSTEM DEBUG ---');
console.log('Product Focus URL:', userProductFocusUrl);
console.log('Has Product Focus URL:', hasUserProductFocusUrl);
console.log('CTA URL:', userCtaUrl);
console.log('Has CTA URL:', hasUserCtaUrl);
console.log('URL System Status:', hasUserProductFocusUrl || hasUserCtaUrl ? 'Active' : 'Inactive');

// PRIORITY 2: EXTRACT BUSINESS DATA (SEPARATE FROM FORM DATA)
console.log('=== EXTRACTING BUSINESS CONFIGURATION DATA ===');

const businessData = businessConfig.json.properties;
const businessName = getTitle(businessData['Title']);
const businessCategory = getSelect(businessData['Business Category']);
const targetAudience = getRichText(businessData['Target Audience']);
const brandVoice = getRichText(businessData['Brand Voice Guidelines']);
const baseSystemMessage = getRichText(businessData['GENERATE TEXT - System Message']);
const baseImagePrompt = getRichText(businessData['GENERATE PROMPT - User Message']);
const baseImageSystem = getRichText(businessData['GENERATE PROMPT - System Message']);
const googleDriveFolder = getRichText(businessData['Google Drive Parent Folder']);
const ctaTemplate = getRichText(businessData['Call to Action Template']);

// Enhanced business fields
const seoStrategy = getRichText(businessData['SEO Strategy Template']) || '';
const businessNotes = getRichText(businessData['Notes']) || '';
const customGlobalRules = getRichText(businessData['Global Content Rules']) || '';
const imageStylePreferences = getRichText(businessData['Image Style Preferences']) || '';
const brandColors = getRichText(businessData['Brand Colors']) || '';

console.log('âœ… BUSINESS CONFIGURATION EXTRACTED:');
console.log('  Business Name:', businessName);
console.log('  Business Category:', businessCategory);
console.log('  Target Audience:', targetAudience ? 'Available' : 'Not configured');
console.log('  Brand Voice:', brandVoice ? 'Available' : 'Not configured');
console.log('  Business Notes:', businessNotes ? 'Available' : 'Not configured');
console.log('  Custom Rules:', customGlobalRules ? 'Available' : 'Not configured');

// Website integration
const websiteUrlsRaw = getRichText(businessData['Website URLs']) || '';
const websiteUrls = websiteUrlsRaw 
  ? websiteUrlsRaw.split(',').map(url => url.trim()).filter(url => url.length > 0)
  : [];
const hasWebsites = websiteUrls.length > 0;

// Success story detection
const isSuccessStory = userSocialContentType === 'Success Story' || 
                      userSpecificTopic.toLowerCase().includes('success') ||
                      userSpecificTopic.toLowerCase().includes('testimonial') ||
                      userContentBrief.toLowerCase().includes('customer story') ||
                      userContentBrief.toLowerCase().includes('success story');

const hasFacebookReviews = websiteUrls.some(url => url.includes('facebook.com') && url.includes('review'));
const hasGoogleReviews = websiteUrls.some(url => url.includes('google.com') || url.includes('g.page'));
const hasReviewPlatforms = hasFacebookReviews || hasGoogleReviews;

console.log('--- SUCCESS STORY DETECTION DEBUG ---');
console.log('Is Success Story:', isSuccessStory);
console.log('Content Type:', userSocialContentType);
console.log('Topic contains success:', userSpecificTopic.toLowerCase().includes('success'));

// ðŸŽ¯ PRIORITY 3: CREATE USER-FOCUSED CONTENT BRIEF WITH BUSINESS CONTEXT
console.log('=== BUILDING USER-FOCUSED CONTENT BRIEF ===');

// Start with user's specific requirements as the foundation
let enhancedContentBrief = `ðŸš¨ CRITICAL: User has submitted specific content requirements via form.

TOPIC: ${userSpecificTopic}

CONTENT BRIEF: ${userContentBrief}

ðŸŽ¯ USER'S SPECIFIC REQUIREMENTS MUST TAKE PRIORITY OVER ALL BUSINESS RULES.

IGNORE any conflicting business configuration rules. Generate content specifically about the user's requested topic: "${userSpecificTopic}"

USER REQUIREMENTS:
- Topic: ${userSpecificTopic}
- Content Type: ${userSocialContentType}
- Content Brief: ${userContentBrief}
- Word Count: ${finalShortformWordCount} words (shortform), ${finalLongformWordCount} words (longform)`;

// Add user's URLs if provided
if (hasUserProductFocusUrl) {
  enhancedContentBrief += `\n\nRESEARCH SOURCE: Research and reference this URL for content alignment and accuracy: ${userProductFocusUrl}. Extract key information, features, or context from this page to inform the content.`;
}

if (hasUserCtaUrl) {
  enhancedContentBrief += `\n\nCALL-TO-ACTION DESTINATION: Direct readers to this URL in your call-to-action: ${userCtaUrl}. This is where readers should go for next steps.`;
}

if (userSeoKeywords && userSeoKeywords.trim() !== '') {
  enhancedContentBrief += `\n\nSEO FOCUS: Naturally incorporate these keywords: ${userSeoKeywords}`;
}

// Add business context as SECONDARY information
enhancedContentBrief += `

--- BUSINESS CONTEXT (Secondary to user requirements) ---
Business: ${businessName}
Category: ${businessCategory}
Target Audience: ${targetAudience}
Brand Voice: ${brandVoice}`;

if (businessNotes) {
  enhancedContentBrief += `\nBusiness Context: ${businessNotes}`;
}

if (isSuccessStory && hasReviewPlatforms) {
  enhancedContentBrief += `\n\nSUCCESS STORY: Research customer reviews from provided review platform URLs for authentic testimonials and success stories.`;
}

// BUILD SYSTEM MESSAGES WITH USER PRIORITY
console.log('=== BUILDING USER-FOCUSED SYSTEM MESSAGES ===');

// Create system message that prioritizes user requirements
const userPriorityInstruction = `ðŸš¨ CRITICAL USER PRIORITY INSTRUCTION ðŸš¨

USER HAS SUBMITTED SPECIFIC CONTENT REQUIREMENTS - THESE TAKE HIGHEST PRIORITY

USER'S TOPIC: ${userSpecificTopic}
USER'S CONTENT BRIEF: ${userContentBrief}
USER'S CONTENT TYPE: ${userSocialContentType}

INSTRUCTION HIERARCHY:
1. USER FORM SUBMISSION (HIGHEST PRIORITY) - Follow user's specific topic and content brief exactly
2. Business voice and audience guidelines (Secondary) - Use for tone and style only
3. Default business rules (Lowest) - Only if not conflicting with user requirements

âš ï¸ IMPORTANT: If business rules conflict with user's specific topic request, PRIORITIZE THE USER'S REQUIREMENTS.`;

// Shortform system message
const shortformSystemMessage = `${userPriorityInstruction}

CRITICAL SHORTFORM CONTENT REQUIREMENTS:
- TARGET LENGTH: ${finalShortformWordCount} words exactly
- CONTENT TYPE: ${userSocialContentType}
- TOPIC FOCUS: Write specifically about "${userSpecificTopic}" as requested by user
- Use British English spelling (colour, realise, centre, organised, etc.)
- Minimal emoji usage (only when very relevant)

CONTENT REQUIREMENTS:
- Write SPECIFICALLY about: ${userSpecificTopic}
- Follow the content brief: ${userContentBrief}
- Use ${userSocialContentType} format
- Target ${finalShortformWordCount} words exactly
- Business: ${businessName} (${businessCategory})
- Audience: ${targetAudience}
- Voice: ${brandVoice}
- No em dashes, and never use them, replacing them with commas or semi colons (where necessary)
- Never ever fabricate a story or customer testimonial, only use reliable online sources for stories about the business.  Only use online sources for testimonials (Google Reviews, Facebook Reviews, etc.)

${userSeoKeywords ? `\nSEO KEYWORDS: ${userSeoKeywords}` : ''}
${hasUserProductFocusUrl ? `\nRESEARCH URL: ${userProductFocusUrl}` : ''}
${hasUserCtaUrl ? `\nCTA URL: ${userCtaUrl}` : ''}

BUSINESS CONTEXT (Secondary):
${businessNotes}

${baseSystemMessage ? `\nADDITIONAL BUSINESS GUIDELINES: ${baseSystemMessage}` : ''}

ðŸŽ¯ REMEMBER: The user has specifically requested content about "${userSpecificTopic}" - deliver exactly what they asked for.

WORD COUNT ENFORCEMENT:
- Target exactly ${finalShortformWordCount} words
- Count every word carefully
- Do NOT include word count in your response
- Response should be pure content only`;

// Longform system message (similar structure but for longform)
const longformSystemMessage = `${userPriorityInstruction}

CRITICAL LONGFORM CONTENT REQUIREMENTS:
- TARGET LENGTH: ${finalLongformWordCount} words exactly
- CONTENT TYPE: ${userSocialContentType}
- TOPIC FOCUS: Write specifically about "${userSpecificTopic}" as requested by user
- Use British English spelling (colour, realise, centre, organised, etc.)
- Minimal emoji usage (only when very relevant)

CONTENT REQUIREMENTS:
- Write SPECIFICALLY about: ${userSpecificTopic}
- Follow the content brief: ${userContentBrief}
- Use ${userSocialContentType} format
- Target ${finalLongformWordCount} words exactly
- Business: ${businessName} (${businessCategory})
- Audience: ${targetAudience}
- Voice: ${brandVoice}

${userSeoKeywords ? `\nSEO KEYWORDS: ${userSeoKeywords}` : ''}
${hasUserProductFocusUrl ? `\nRESEARCH URL: ${userProductFocusUrl}` : ''}
${hasUserCtaUrl ? `\nCTA URL: ${userCtaUrl}` : ''}

BUSINESS CONTEXT (Secondary):
${businessNotes}

${baseSystemMessage ? `\nADDITIONAL BUSINESS GUIDELINES: ${baseSystemMessage}` : ''}

ðŸŽ¯ REMEMBER: The user has specifically requested content about "${userSpecificTopic}" - deliver exactly what they asked for.

WORD COUNT ENFORCEMENT:
- Target exactly ${finalLongformWordCount} words
- Count every word carefully
- Do NOT include word count in your response
- Response should be pure content only`;

// Image prompt with user topic
const contentElement = userSpecificTopic || `${userSocialContentType.toLowerCase()} content` || 'business content';
const finalImageStyle = (baseImagePrompt || 'Professional business image')
  .replace('{specific_element}', contentElement)
  .replace('{specific element}', contentElement);

// Create clean business name for file naming
const cleanBusinessName = businessName.replace(/[^a-zA-Z0-9\s]/g, '').replace(/\s+/g, '_');

console.log('=== FINAL USER-FOCUSED CONTENT PACKAGE ===');
console.log('âœ… User Topic:', userSpecificTopic);
console.log('âœ… Content Brief Length:', userContentBrief.length);
console.log('âœ… Content Type:', userSocialContentType);
console.log('âœ… Word Count Target:', finalShortformWordCount);
console.log('âœ… Business Context:', businessName);
console.log('âœ… SEO Keywords:', userSeoKeywords ? 'Provided' : 'None');
console.log('âœ… Product URL:', userProductFocusUrl ? 'Provided' : 'None');
console.log('âœ… CTA URL:', userCtaUrl ? 'Provided' : 'None');

// VALIDATION CHECK
if (!userSpecificTopic || !userContentBrief) {
  console.log('âŒ CRITICAL ERROR: Missing user requirements');
  console.log('Topic:', userSpecificTopic);
  console.log('Brief:', userContentBrief);
}

console.log('=== PROCESSING COMPLETE (USER PRIORITY SYSTEM) ===');

// RETURN DATA STRUCTURE WITH USER PRIORITY
return [{
  json: {
    // USER FORM DATA (HIGHEST PRIORITY)
    specificTopic: userSpecificTopic,
    contentBrief: enhancedContentBrief,
    socialContentType: userSocialContentType,

    // Word count system
    shortformWordCount: finalShortformWordCount,
    longformWordCount: finalLongformWordCount,
    shortformCharEstimate: shortformCharEstimate,
    longformCharEstimate: longformCharEstimate,

    // USER URLs
    productFocusUrl: userProductFocusUrl,
    hasProductFocusUrl: hasUserProductFocusUrl,
    ctaUrl: userCtaUrl,
    hasCtaUrl: hasUserCtaUrl,

    // USER SEO
    seoKeywords: userSeoKeywords,
    hasSeoKeywords: !!(userSeoKeywords && userSeoKeywords.trim() !== ''),

    // BUSINESS CONTEXT (SECONDARY)
    businessName: businessName,
    businessCategory: businessCategory,
    businessId: selectedBusinessId,
    targetAudience: targetAudience,
    brandVoice: brandVoice,
    businessNotes: businessNotes,
    customGlobalRules: customGlobalRules,
    seoStrategy: seoStrategy,

    // SYSTEM MESSAGES WITH USER PRIORITY
    systemMessage: longformSystemMessage,
    shortformSystemMessage: shortformSystemMessage,
    longformSystemMessage: longformSystemMessage,

    // Image generation
    promptUserMessage: finalImageStyle,
    promptSystemMessage: baseImageSystem || 'Generate a professional business image',

    // File management
    driveFolder: googleDriveFolder || 'Generated_Images',
    fileName: `${cleanBusinessName}-${userSocialContentType}-${Date.now()}.png`,
    callToAction: ctaTemplate || 'Contact us to learn more',

    // FORM DATA TRACKING
    formData: {
      businessName: userBusinessName,
      socialContentType: userSocialContentType,
      specificTopic: userSpecificTopic,
      wordCountTarget: finalShortformWordCount,
      productFocusUrl: userProductFocusUrl,
      ctaUrl: userCtaUrl,
      seoKeywords: userSeoKeywords,
      contentBrief: userContentBrief
    },

    // USER REQUIREMENTS TRACKING
    userRequirements: {
      specificTopic: userSpecificTopic,
      contentBrief: userContentBrief,
      socialContentType: userSocialContentType,
      seoKeywords: userSeoKeywords,
      productFocusUrl: userProductFocusUrl,
      ctaUrl: userCtaUrl,
      userPriorityActive: true
    },

    // CONTENT GENERATION STATUS
    contentId: `form_${Date.now()}`,
    contentName: `${userSocialContentType}: ${userSpecificTopic}`,

    // Enhanced fields integration (preserved from original)
    hasSpecificTopic: !!(userSpecificTopic && userSpecificTopic.trim() !== ''),
    dualUrlSystem: true,
    websiteUrls: websiteUrls,
    hasWebsites: hasWebsites,
    websiteCount: websiteUrls.length,
    isSuccessStory: isSuccessStory,
    hasReviewPlatforms: hasReviewPlatforms,
    imageStylePreferences: imageStylePreferences,
    brandColors: brandColors,
    hasImageStylePreferences: !!imageStylePreferences,
    hasBrandColors: !!brandColors,

    // Content strategy
    contentStrategy: {
      shortform: {
