      dataRows.push(rowObject);
      console.log(`âœ… Row ${i}: ${rowObject['Specific Topic']} - VALID (included)`);
    } else {
      console.log(`âš ï¸ Row ${i}: Missing topic or brief - skipped`);
    }
  }

  console.log('=== CONTENT REQUESTS CSV PARSING RESULTS ===');
  console.log('Total valid content requests found:', dataRows.length);
  console.log('Topics:', dataRows.map(row => row['Specific Topic']));

  if (dataRows.length === 0) {
    throw new Error('No valid content requests found in CSV file. Check that rows have Specific Topic and Content Brief.');
  }

  // Return array of content request objects for SplitInBatches
  console.log('âœ… Content requests CSV parsing successful - returning', dataRows.length, 'content requests');
  return dataRows.map(row => ({ json: row }));

} catch (error) {
  console.error('âŒ Content requests CSV parsing error:', error.message);
  console.error('CSV content that failed:', csvData.csvContent.substring(0, 500));

  return [{
    json: {
      error: error.message,
      csvFile: csvData.csvFileName || 'unknown',
      success: false,
      csvContentPreview: csvData.csvContent ? csvData.csvContent.substring(0, 200) : 'No content',
      detectedHeaders: csvData.csvContent ? csvData.csvContent.split('\n')[0] : 'No headers'
    }
  }];
}

